import React, { useEffect, useState } from 'react';
import { HashRouter, Route, Routes, Link, Outlet } from 'react-router-dom';
import App from './App';
import Appnew from './Appnew';
import RegisterPage from './RegisterPage';
import Dashboard from './Dashboard';
import Invalid from './Invalid';
import Home from './Home';
//import Logout from './Logout';
//lazy load
const Logout = React.lazy(() => import("./Logout"));

function Routing() {
    const [activeMenu, setActiveMenu] = useState("");
    // const menuItems = [
    //     { key: "crud", "displayName": "CRUD", isDefaut: true },
    //     { key: "login", "displayName": "Login", isDefaut: false },
    // ];

    // useEffect(() => {
    //     const defaultMenu = menuItems.filter((menu) => menu.isDefaut);
    //     setActiveMenu(defaultMenu[0].key);
    // }, []);

    return (
        <div>

            <HashRouter>
                {/* <ul>
                    {
                        menuItems.map((menu) =>
                            <Link to={'/' + menu.key}>{menu.displayName}</Link>
                        )
                    }
                </ul> */}

                <Routes>
                
                    <Route path='/' element={
                        <React.Suspense fallback={<h2>Loading....</h2>}>
                            <Link to='/crud'>CRUD</Link> { }
                            <Link to='/login'>Login</Link> { }

                            <Outlet />
                            <Home/>
                            
                        </React.Suspense>
                    } />
                    <Route path='/login' element={<App />} />
                    <Route path='/crud' element={<Appnew />} />
                    <Route path='/register' element={<RegisterPage />} />
                    {/* <Route path='/' element={<Home />} /> */}
                    <Route path='/dashboard' element={
                        sessionStorage.getItem('loginDetails') !== null ?
                            <Dashboard /> : <App />} />

                    <Route path='*' element={<Invalid />} />
                    
                    <Route path='/logout' element={
                        <React.Suspense fallback={<h2>Loading....</h2>}>
                            <Logout />
                        </React.Suspense>
                    } />


                </Routes>


            </HashRouter>



        </div>

    );
}

export default Routing;
