import { useState } from "react";

function EmpDetails(){

    const empName="";
//destructuring method
    const [name,userNameSet] =useState("");
    const [pno,setpno] =useState("");
    const [vpno,setvpno] =useState("");
    /*const def=useState("");
    const pno=def[0];
    const setpno=def[1];*/
    const [email,updateEmail] =useState("");
    //const [email,updateEmail] =useState("");
    
    function uservalue(event){
        let phone=event.target.value;
        if(!Number(phone))
        {
            //console.log("Error,Please fill it with numbers only");
            let error=<strong>Error,Please fill it with numbers only</strong>;
            setpno(error);
        }
        
            setvpno(phone);
        
        
        
    }
    function userName(event){
         userNameSet(event.target.value);        
    }
    return(
        <div className="empFull">
            <h1 className="empHeader">Employee Registration Form</h1>
            <form className="empForm was-validated">
                
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label"> Employee Name</label>
                    <input type="text" className="form-control" id="exampleInputEmail1" required onChange={userName} value={name} aria-describedby="emailHelp"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Employee address</label>
                    <input type="text" className="form-control" onChange={event => updateEmail(event.target.value)} value={email} id="exampleInputEmail1" required aria-describedby="emailHelp"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Employee Phonenumber</label>
                    <input type="text" className="form-control"  id="exampleInputEmail1" required onChange={uservalue} value={vpno} aria-describedby="emailHelp"/>
                    {pno}
                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Employee Emailid</label>
                    <input type="email" className="form-control" id="exampleInputEmail1" required aria-describedby="emailHelp"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Email Designation</label>
                    <input type="text" className="form-control" id="exampleInputEmail1" required aria-describedby="emailHelp"/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    );
}

export default EmpDetails;
