import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { useEffect, useState } from 'react';
import RouteEmp from './RouteEmp';
import RouteUser from './RouteUser';
import RouteHook from './RouteHook';
import { BrowserRouter, HashRouter, Link, Route, Routes } from 'react-router-dom';

function Routing() {
    const [activeMenu, setActiveMenu] = useState("");

    const menuItems=[
        {key:"employees","displayName":"Employees",isDefaut:true},
        {key:"users","displayName":"User",isDefaut: false},
        {key:"hooks","displayName":"Hooks", isDefaut: false}
    ];

    useEffect(()=>{
        const defaultMenu= menuItems.filter((menu)=>menu.isDefaut);
        setActiveMenu(defaultMenu[0].key);
    },[]);

    return (
        <div>
            {/* <ul>
            {
                menuItems.map((menu)=>
                <Link to={'/' + menu.key}>{menu.displayName}</Link>
                )
            }
            </ul>
            <BrowserRouter>
                <Routes>
                    <Route path='/employees' element={<RouteEmp/>}/>
                    <Route path='/users' element={<RouteUser/>}/>
                    <Route path='/hook' element={<RouteHook/>}/>
                </Routes>
            </BrowserRouter> */}
            

            <HashRouter>
        <ul>
          {
            menuItems.map((menu) =>
              <li className='menu-item'>
                <Link to={'/' + menu.key}>{menu.displayName}</Link>
                {/* <a href={'/'+menu.key}>{menu.displayName}</a> */}
              </li>
            )
          }
        </ul>
        <Routes>
          <Route path='/employees' element={<RouteEmp />} />
          <Route path='/users' element={<RouteUser />}>
            {/* <Route path='all' element={<UserList />} />
            <Route path='new' element={<UserDetails />} />
            <Route path=':id' element={<UserDetails />} /> */}
          </Route>
          <Route path='/hooks' element={<RouteHook />} />
          <Route path='/' element={<RouteEmp />} />
        </Routes>

        {/* <Swicth>
          <Route path='/employees'>
            <EmployeeContainer />
          </Route>
          <Route path='/users'>
            <UserContainer />
          </Route>
        </Swicth> */}
      </HashRouter>
            
            

        </div>

    );
}

export default Routing;
