import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { useEffect, useState } from 'react';
import RouteEmp from './RouteEmp';

function Routing() {
   // const [activeMenu, setActiveMenu] = useState("employees");//set emp as default
    const [activeMenu, setActiveMenu] = useState("");
    const menuItems=[
        {key:"employees","displayName":"Employees",isDefaut:true},
        {key:"users","displayName":"User",isDefaut: false},
        {key:"hooks","displayName":"Hooks", isDefaut: false}
    ];

    useEffect(()=>{
        const defaultMenu= menuItems.filter((menu)=>menu.isDefaut);
        setActiveMenu(defaultMenu.key);
    },[]);
    return (
        <div>
            {/*dynamic li coding */}
            {
                menuItems.map((menu)=>
                <li onClick={()=>setActiveMenu(menu.key)}>{menu.displayName}</li>
                )
            }

            {// static li coding 
            /*<ul>
                <li onClick={()=>setActiveMenu("employees")}>Employees</li>
                <li onClick={()=>setActiveMenu("users")}>User</li>
                <li onClick={()=>setActiveMenu("hooks")}>Hooks</li>
            </ul>*/}
            {
                //conditional rendering  
                //if we want else part use ternary opr
                // a>5 ?true:(a>3?greater:null)
                activeMenu === "employees" &&  <RouteEmp/>
            }

            {
                activeMenu === "users" &&

                <div className="container">
                    <div className="row">
                        <div className='col-md-6 col-sm-12'>
                            user details

                        </div>
                        <div className='col-md-6 col-sm-12 listdetails'>
                            user list details
                        </div>

                    </div>
                </div>
            }

            {
                activeMenu === "hooks" &&

                <div className="container">
                    <div className="row">
                        <div className='col-md-6 col-sm-12'>
                            Welcome to hooks

                        </div>
                        <div className='col-md-6 col-sm-12 listdetails'>
                            Welcome to hooks list
                        </div>

                    </div>
                </div>
            }

        </div>

    );
}

export default Routing;
