import { useState } from "react";
import { useNavigate } from "react-router-dom";

function RegisterPage() {
  const [regname,setName]=useState();
  const [number,setPhone]=useState();
  const [email,setMail]=useState();
  const [username,setUsername]=useState();
  const [password,setPassword]=useState();
  const [userList,setRegList]=useState([]);

  const navigate=useNavigate();

  function addEmployee(event) {       
   
    var updatedEmpListPush =JSON.parse(JSON.stringify(userList));    

    updatedEmpListPush.push({
        regname: regname,
        number:number,
        email: email,
        username: username,
        password:password
        
    });
    setRegList(updatedEmpListPush);
     console.log("updated state", updatedEmpListPush);
     localStorage.setItem("userList",JSON.stringify(userList));
    event.preventDefault();
    navigate("/login");
    //resetForm();
    //props.onEmployeeListChange(updatedEmpListPush);    
  }
 
 console.log("name is"+regname);
  return (
    <div>
      <div class="row">
        <div class='col-3 welcome'>
          <h2>
            Welcome to Our Site
          </h2>
        </div>
        <div class='col-9 formreg'>
        <div className="register">
      <form className="row g-3">
        <div className="col-md-12">
          <label htmlFor="inputName" className="form-label">Name</label>
          <input type="text" className="form-control" id="inputCity" name={regname} onChange={e=>setName(e.target.value)}/>
        </div>
        <div className="col-12">
          <label htmlFor="inputMobile" className="form-label">Phone Number</label>
          <input type="Number" className="form-control" id="inputMobile" name={number} placeholder="121232" onChange={e=>setPhone(e.target.value)}/>
        </div>
        <div className="col-md-12">
          <label htmlFor="inputEmail4" className="form-label">Email</label>
          <input type="email" className="form-control" id="inputEmail4" name={email} onChange={e=>setMail(e.target.value)}/>
        </div>
        <div className="col-12">
          <label htmlFor="inputAddress" className="form-label">Username</label>
          <input type="text" className="form-control" id="inputAddress" placeholder="a@gmail.com" name={username} onChange={e=>setUsername(e.target.value)}/>
        </div>
        <div className="col-md-12">
          <label htmlFor="inputPassword4" className="form-label">Password</label>
          <input type="password" className="form-control" id="inputPassword4" name={password} onChange={e=>setPassword(e.target.value)}/>
        </div>

        <div className="col-6">
          <button type="submit" className="btn btn-primary" onClick={addEmployee}>Sign in</button>
          <p className="signup">Already Have an Account <a href="#/login">Click Here</a></p>
        </div>
      </form>
    </div>
        </div>
      </div>
      
    </div>
    
  );
}

export default RegisterPage;
