import React, { useEffect, useState } from 'react';

function Home(){
    const menuItems = [
        { key: "crud", "displayName": "CRUD", isDefaut: true },
        { key: "login", "displayName": "Login", isDefaut: false },
    ];

    useEffect(() => {
        const defaultMenu = menuItems.filter((menu) => menu.isDefaut);
        setActiveMenu(defaultMenu[0].key);
    }, []);

    return(
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div className="container">
                    <span style={{ color: 'white', fontSize: '1.6rem' }}>Welcome To Amadis</span>
                    <div className=" navbar-collapse ulDiv" id="navbarResponsive">
                        <ul className="navbar-nav">
                            {menuItems.map((menu, i) =>
                                <li className='nav-item mx-0 mx-lg-1' key={i}>
                                    <Link to={'/' + menu.key} className='li'>{menu.displayName}</Link>
                                </li>
                            )
                            }
                        </ul>
                    </div>
                </div>
            </nav>
            <h1 className="home">Welcome to Amadis Global</h1>
        </div>
        
    )
}
export default Home;
