import React, { useEffect, useState } from 'react';
import { BrowserRouter, HashRouter, Link, Route, Routes } from 'react-router-dom';
import App from './App';
import RegisterPage from './RegisterPage';
import Dasbboard from './Dashboard';
import Invalid from './Invalid';
//import HookContainer from './HookContainer';
//lazy load


function Routing() {
    const [activeMenu, setActiveMenu] = useState("");

    const menuItems = [
        { key: "login", "displayName": "login", isDefaut: false },
        { key: "register", "displayName": "register", isDefaut: false },
        { key: "dashboard", "displayName": "dashboard", isDefaut: true }
    ];

    useEffect(() => {
        const defaultMenu = menuItems.filter((menu) => menu.isDefaut);
        setActiveMenu(defaultMenu[0].key);
    }, []);

    return (
        <div>
            
            <HashRouter>
                <ul>
                    {
                        menuItems.map((menu) =>
                            <li className='menu-item'>
                                <Link to={'/' + menu.key}>{menu.displayName}</Link>
                            </li>
                        )
                    }
                </ul>
                <Routes>
                    <Route path='/login' element={<App />} />
                    <Route path='/register' element={<RegisterPage />} /> 
                    <Route path='/dashboard' element={<Dasbboard />} />
                    <Route path='*' element={<Invalid />} /> 
                    <Route path='/' element={<App />} />  

                   
                    {/* <Route path='/hooks' element={
                        <React.Suspense fallback={<h2>Loading....</h2>}>
                            <HookContainer />
                        </React.Suspense>
                    } /> */}
                    
                   
                </Routes>

                
            </HashRouter>



        </div>

    );
}

export default Routing;
